	`publish_down` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
	PRIMARY KEY (`id`),
	INDEX `idx_access` (access),
	INDEX `idx_checkout` (checked_out),
	INDEX `idx_state` (published),
